import React from "react";
import "../App.css";

class PersonCard extends React.Component {
  render() {
    const { lastName, firstName, age, hairColor } = this.props;
    return (
      <div id="container">
        <div id="content">
          <p>
            <h1>
              {lastName},{firstName}
            </h1>
            <h3> Age: {age} </h3>     
            <h3> Hair color: {hairColor} </h3>  
          </p>
        </div>
      </div>
    );
  }
}
export default PersonCard;
