import React from "react";

class PersonCard extends React.Component {
  render() {
    const { lastName, firstName, age, hairColor } = this.props;
    return (
      <div>           
        <h1>
          {lastName},{firstName}
        </h1>
        <h1> Age: {age} </h1>     
        <h1> Hair color: {hairColor} </h1>  
      </div>
    );
  }
}
export default PersonCard;
